INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000018','논산 계백의 혼이 살아 숨쉬는 솔바람 길','논산 계백의 혼이 살아 숨쉬는 솔바람길','돈암서원~충곡서원~백제군사박물관~탑정호수변생태공원~신풍리마애불~휴정서원','충남 논산시','쉬움','5~10Km미만','6','수려한 자연경관을 자랑하는 탑정호 주변 전통문화 및 관광자원을 활용하여 아름다운 역사문화를 보고 즐기며 걷는 자연친화적인 시설로 연산면 임리, 고정리, 부적면 충곡리 계백장군 묘역 일원에 조성되는 산책로 및 쉼터이다. ○ 전통문화 : 충곡 서원, 휴정서원, 계백장군 묘역 ○ 생태자원 : 탑정호 수변생태공원 ○ 교통역사 : 휴정서원, 충곡서원등 유교 문화재, 영사암, 마애불상 ○ 체험자원 : 계백장군유적지체험관광, 탑정호 자전거체험 ○ 수락산을 산책하며 바라볼 수 있는 수려한 경관을 자랑하는 논산의 대표 관광지 탑정호 ○ 연꽃등 다양한 생태자원을 관람할 수 있는 생태공원','2시간','없음(사전준비)','백제군사박물관','백제군사박물관','충남 논산시 연산면 고정리 산 13-1',36.1955016,127.1830362);
