INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000021','서천 생태원길','배롱나무 코스','금강하굿둑 관광단지 입구 → 금강하굿둑 전망대(0.7㎞) → 금강하굿둑 놀이동산 단지(0.8㎞)','충남 서천군','보통','1~5Km미만','1.5','- 금강하구, 서해안 등을 한눈에 볼 수 있는 전망 제공하며, 길을 따라 식재한 배롱나무 또한 볼거리를 제공한다 - 금강하굿둑 : 4대강중의 하나인 금강은 충청도와 전라북도를 에두르고 휘돌며 구비마다 충청도의 아름다운 경관을 감싸안고 흐르는 종점 - 조류생태전시관 : 철새탐조방법과 실시간 철새탐조, 철새들의 낙원 서천금강의 아름다운 자연생태를 체험할 수 있는 전시관','40분','없음','금강하굿둑 놀이동산 공원','금강하굿둑 놀이동산 공원','충남 서천군 마서면 도삼리 68',36.018195,126.7351366);
