INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000022','서천 남산길','01코스','서천 특화시장(0km)→판교천(1km)→남산성(2.5km)→레포츠 공원(10km)→서천특화시장(13km)','충남 서천군','보통','10~15Km미만','13','서천읍 남산리와 마서면 봉남리에 걸쳐있는 산으로 이산의 정상부에 둘레 약468m에 이르는 성곽을 두른 것이 서천 남산성이다. 예전부터 마을사람들이 사용하던 산길을 정비하여 남산길을 만들었으며 서천군의 중심지를 한눈에 볼 수 있는 특징이 있다. 또한 읍내 중심에서 출발해 편리하고 그 주변 곳곳을 살펴볼 수 있도록 코스가 넓게 짜여있어 트래킹과 산행이 동시에 가능한 코스이다.','3시간 30분','약수대 1개소','서천 특화시장, 판교천 화장실, 레포츠 공원 3개소','서천 특화시장, 서천 여자 정보고 앞 사거리 2곳','충남 서천군 서천읍 남산리 112-21',36.0670671,126.6902387);
