INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000023','철새나그네길','01코스 붉은 낭만길','홍원항(0km)~춘장대 해수욕장(1.2km)~부사호(3.77km)','충남 서천군','보통','1~5Km미만','3.77','서천 철새 나그네길의 1코스인 붉은 낭만길은 홍원항에서 시작하여 춘장대 해수욕장을 거쳐 부사호에서 끝이 나는 3.77km의 길로 붉은 등대와 이국적 경치의 낭만, 추억과 다양한 축제가 가득한 길이다. 홍원항에서는 바다의 생동감을, 춘장대 해수욕장에서는 낭만과 젊음을, 부사호에서는 잔잔한 서해바다를 느낄 수 있다. 해안 길로 형성된 붉은 낭만길은 만조때는 바다의 생동감, 간조때는 갯벌의 풍성함을 볼 수 있고 저녁에는 해질녁 노을이 장관을 이루는 팔색조의 걷기 길이다.','1시간','없음','2곳(춘장대 해수욕장, 홍원항)','2곳(춘장대 해수욕장, 홍원항)','충남 서천군 장항읍 송림리 731-21',36.0263316,126.6634073);
