INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000026','탕정둘레길','탕정둘레길','탕정면사무소~비석산(01코스,4.7km)~삼성lcd기숙사(02코스,3.2km)~꾀꼴산성, 물한산성(03코스, 6.8km)~삼성단지인도(3.3km)','충남 아산시','보통','15~20Km미만','18','아산시 탕정면 명암리 일원에 위치한 삼성 테크노벨리 단지를 중심으로 주변을 둘러보고 물한산성과 꾀꼴산성을 연계하여 탕정면 일대를 둘러볼수 있는 코스이다. 탕정둘레길은 주민들이 산책하기 적합하게 4개 코스로 구분했지만 순환해 걷는 코스인 만큼 하루 시간내어 걷기 좋다.','8시간','식수보급처가 없으니 개별적으로 준비또는 마을주변 편의점을 이용','2코스 중간(탕정면 명암리 마을회관) 화장실 이용','마을주변으로 편의점 분포','충남 아산시 음봉면 동암리 산 78-10',36.8368321,127.0503758);
