INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000027','천년의 숲길','천년비손길, 봉곡사 솔바람길, 긴골재길, 천년물결길','1. 천년비손길(13km) : 봉곡사-봉수산-오형제고개-누에마을-배골마을-송악저수지(궁평저수지)-봉곡사 2. 봉곡사 솔바람길(3.5km) : 봉곡사-봉곡사임도-누에마을 3. 긴골재길(5.6km) : 송남휴게서-황산등산로-긴골산-강장고개 4 천년물결길(3.5km) : 송남휴게서-송악저수지 수변길-등산로-송남휴게소','충남 아산시','보통','20~100Km미만','25.6','아산시의 산림자원 및 지역자원과 관광문화자원을 활용, 숲길 조성을 통한 관광자원화 개발과 급증하는 산림휴양 서비스에 충족하고 2013년 안전행정부 공모사업으로 조성된 천년의 숲길은 자연탐방 및 명상 사색코스(천년비손길, 솔바람길)와 임도를 활용한 수변둘레길(천년물결길), 마을주변 지역주민들이 많이 이용하는 긴골산 등으로 구성되어 있다.','12시간','출발점인 봉곡사에는 약수터, 송남휴게소 매점에서 구입가능','출발점인 봉곡사 주차장과 송남휴게소에 조성','출발점인 송남휴게소에서 구입가능','충남 아산시 송악면 동화리 444-2',36.7219226,126.9747064);
