INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000028','마곡사 솔바람길','01코스 백범길, 02코스 명상길, 03코스 송림숲길','백범길 산책코스(3km) 명상산책길 트레킹 코스( 5km) 송림숲길 등산코스(11 km) 마곡사 → 천연송림욕장→ 백련암 → 아들바위→활인봉→전통불교문화원→다비식장→군왕대','충남 공주시','보통','15~20Km미만','19','마곡사 솔바람길은 백범길 산책코스와 명상산책길 트레킹 코스, 송림숲길 등산 코스를 연결한 길이다. 백범길과 명상 산책길은 백범 김구 선생께서 일본군 장교를 살해한 후 마곡사 백련암에서 은거 수행 생활 할 때 구국의 일념으로 사색하는 발자취를 따라 조성됐다. 소나무가 빽빽한 숲길을 걸으며 나라를 걱정했던 뱀범 김구 선생의 마음을 느껴보고 명상에 잠기기 좋은 길이다. 또한 이 코스는 마곡사를 둘러볼 수 있고 인근 자연마을에서 농업체험을 할 수 있다. 다른 길보다 긴시간이 소요되고 코스별로 길의 난이도가 다르므로 자신이 걸을 수 있는 능력에 따라 코스를 선택하는 것이 좋다.','5시간 50분','마곡사 경내 약수','마곡사 경내, 마곡사 관광지 내','마곡사 경내 사찰 매점','충남 공주시 사곡면 운암리',36.5566814,127.0110583);
