INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000032','태조산 솔바람길','01코스','1코스 : 청송사 ~ 구름다리 2.5km 2코스 : 구름다리 ~ 각원사 2.7km','충남 천안시 동남구','보통','5~10Km미만','5.2','충청남도 천안시 유량동에서 안서동으로 이어지는 솔바람길은 완만한 산세로 여성 산행인들이 즐겨 찾는 곳이다. 솔바람길은 주변으로 천안의 역사와 문화를 간직한 곳이다 2시간 이내의 짧은 산행이어서 산책이나 가족 산행으로 알맞다. 천안 시민은 주로 원성동 천안시민체육공원(청송사)에서 구름다리를 거쳐 유량동을 지나 성불사 뒤 능선을 따라 각원사와 유왕골 갈림길에서 각원사쪽으로 내려오는 5.3㎞ 길이의 코스를 이용한다. 솔바람길의 백미는 구름다리위에서 맞는 산바람이다. 산행으로 인한 땀방울이 다리위 산바람으로 인해 시원해지는 느낌은 태조산에서만 맛볼 수 있는 또 하나의 자랑거리이다. 산행은 각원사 주차장에서 각원사쪽으로 바라볼 때 청동대불을 거쳐 좌측능선에 올라선 후 절을 한 바퀴 감싸돌며 능선을 타고 주차장 아래 도로변에 도착하게 되는데 2시간도 채 못걸린다. 굴곡없이 평평하고 한적한 능선길이 부담 없어 비가올 때나 달리 갈 데가 없을 때 찾아볼 만한 친근한 곳이다.','2시간','청송사 입구 ~ 0.4km지점 음수대 위치','','','충남 천안시 동남구 유량동',36.8218605,127.1747521);
