INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000036','가야구곡 녹색길','가야구곡 녹색길','덕산온천 관광안내소 ~ 2.6km ~ 옥계저수지 ~ 1.8km ~ 옥계리 마을회관 ~ 0.4㎞ ~ 옥병계 ~ 1.2km ~ 석문담 ~ 1.6km ~ 남연군묘 ~ 0.6km ~ 와룡담','충남 예산군','보통','15~20Km미만','16','덕산도립공원내 가야산 자락의 아름다운 비경 아홉곳에 대한 역사·문화·생태 자원을 활용한 스토리텔링 주제로 옛 가야9곡길 복원하였으며 가야구곡의 유래는 조선 영조 때 병조판서를 지낸 병계 윤봉구(尹鳳九, 1681~1767)선생이 가야계곡의 아름다운 비경인 아홉 곳(관어대, 옥병계, 습운천, 석문담, 영화담, 탁석천, 와룡담, 고운벽, 옥량폭)을 ‘가야구곡’이라 칭하고 문집에 기록해 놓음으로써 비롯되었다. 또한 가야구곡을 따라 산재해 있는 관광자원인 덕산온천, 남연군묘, 덕산향교, 헌종태실, 광덕사, 보덕사, 옥계저수지, 상가저수지, 가야산등 덕산 지역의 역사와 문화를 함께 탐방할 수 있다 .','4시간','덕산온천관광안내소, 광덕사 약수, 덕산도립공원 관리사무소, 가야구곡농산물판매장','덕산온천관광안내소, 덕산도립공원 관리사무소, 남연군묘, 미륵불, 가야구곡농산물판매장','출발지점 스파캐슬앞, 중간에는 없음, 종점 덕산도립공원관리사무소 앞','충남 예산군 덕산면 옥계리 591-1',36.7057856,126.6452567);
