INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000061','DMZ펀치볼둘레길','01코스 평화의 숲 길','안내센터~와우산(4.3km)~이현저수지(5.4km)~수뢰골숲길(7.0km)~동막동(9.0㎞)~ 잣나무숲길(10.5㎞)~현리 시내(12.0㎞)~양구통일관(14.0㎞)','강원 양구군','쉬움','10~15Km미만','14','DMZ펀치볼둘레길 중에서 최북단 숲길로 상처투성이가 된 불망비와 와우산에 새겨진 ''평화''문자, ''한반도 지형'', 숲길 곳곳에 숨어 있는 벙커와 교통호, 월북방지판 등 한국전쟁의 흔적을 볼 수 있으며, 을지전망대와 제4땅굴이 가까이에 있어 분단의 현실을 실감하고 평화의 중요성을 느낄 수 있다. 와우산의 아름다운 자작나무 숲 속에서 산림욕을 즐기며 해안분지 전체를 조망할 수 있다. DMZ펀치볼둘레길은 지뢰위험지대와 군사시설보호구역 통과구간이 많으므로 반드시 숲길체험지도사의 안내에 따라야 한다.','약 4시간 20분','현3리, 현1리 마을(가정집)','와우산 입구, 수뢰골 벙커','시발점인 양구통일관과 현리 시내에만 있음.','강원 양구군 해안면 후리 621',38.2901289,128.145418);
--행 62
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000062','DMZ펀치볼둘레길','02코스 오유밭길','안내센터~동막동(1.5km)~상평 쉼터(4.3km)~대우산 육각정(5.0km)~화장실(5.3㎞)~ 오유저수지(8.2㎞)~만대2교(10.4㎞)~선사유적지(13.0㎞)~양구통일관(14.6㎞)','강원 양구군','보통','10~15Km미만','14.6','가칠봉 산자락의 황톳길을 걸으면서 해안분지 풍광을 한 눈에 내려다 볼 수 있으며, 지뢰제거 후 조성한 숲길에서 중부지방 고유의 식생과 야생동물을 관찰할 수 있다. 정부의 이주정책으로 조성된 만대리 주민들의 생활상을 엿보며, 논두렁길과 만대천 둑방길을 지나 선사유적지를 돌아본다. 숲길과 마을길이 고루 섞여있어 지루하지 않은 트레킹을 제공한다. 생각보다 높은 고도와 장시간 트레킹이 진행되는 구간이므로 철저한 준비도 필요한 편이다. DMZ펀치볼둘레길은 지뢰위험지대와 군사시설보호구역 통과구간이 많으므로 반드시 숲길체험지도사의 안내에 따라야 한다.','약 5시간 30분','현3리 마을, 지뢰천골 계곡, 만대리 마을','자작나무 조림지(상상바위)','시발점인 양구통일관과 만대리 마을에만 있음.','강원 양구군 해안면 후리 621',38.2901289,128.145418);
