INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000063','DMZ펀치볼둘레길','03코스 만대벌판길','안내센터~선사유적지(1.6km)~삼거리쉼터(2.4km)~만대마을(4.5km)~DMZ자생식물원(8.0㎞)~ 먼멧재길 합류지(12.0㎞)~삼거리쉼터(14.8㎞)~양구통일관(17.2㎞)','강원 양구군','보통','15~20Km미만','17.2','선사유적지를 지나 삼거리쉼터와 잣나무숲을 거쳐 이주민 정착촌이 있는 만대마을에 이른다. 냉장쉼터 식당에서 DMZ자생식물원에 이르러 만대저수지 육각정에서 북녘 산천을 바라보고 비티민나무와 산채재배단지를 둘러본 후, 영농한계선을 따라 조성된 지뢰밭 숲길을 만난다. DMZ펀치볼둘레길은 지뢰위험지대와 군사시설보호구역 통과구간이 많으므로 반드시 숲길체험지도사의 안내에 따라야 한다. 코스조성이 인삼밭길을 걷는 구간과 숲길로 가는 구간 두 군데로 나뉘어져 있어서 산 속 트레킹을 즐겨하는 분이라면 숲길로 가는 것도 좋은 방법이다.','약 6시간 40분','만대리 마을, 만대지 상류','DMZ자생식물원, 만대저수지, 먼멧재길 합류지','만대리 마을','강원 양구군 해안면 후리 621',38.2901289,128.145418);
