INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000064','DMZ펀치볼둘레길','04코스 먼멧재길','안내센터~탁수저감침사지(2.1km)~지뢰밭출입통제소(3.0km)~폐막사(6.0km)~통일봉헬기장(7.5㎞)~ 먼멧재 전차방어벽(10.5㎞)~만대벌판길 합류(11.4㎞)~양구통일관(16.2㎞)','강원 양구군','어려움','15~20Km미만','16.2','시발점에서 전쟁기념관을 둘러보고 마을 등산로에 올라 자작나무 숲길을 걷다보면 인제로 통하는 도로에 접해있는 ''소양강 탁수저감 침사지''와 소공원에 이른다. 이곳부터 ''백두대간트레일'' 시발점으로 물골교를 건너자마자 지뢰위험 경고판과 양편으로 지뢰철조망이 나타나며, 출입통제 초소와 차단기가 설치된 군 작전도로에 접어든다. 작전도로는 원시림에 가까운 나무들이 그늘을 이루는 가운데, 인제군 경계 전차방어벽을 지나면 심적습지와 폐쇄된 군막사를 돌아 오르게 된다. 산 능선에는 헬기장, 벙커, 교통호가 산재해 있어 군의 통제에 따라야 하며, 먼멧재길 산 능선이 곧 민간인통제선을 따라 걷는 코스이다. 대암산 자락이 길게 뻗은 능선 길을 걸으면 겹겹이 포개진 북한 산하, 쾌청한 날에는 멀리 금강산까지 보이며, 남쪽으로는 향로봉과 점봉산, 설악산까지 관측된다. 먼멧재 정상의 전차방어벽에서 오리나무숲 터널읠 따라 만대벌판길로 합류하는 계곡을 내려오면서 산림유전자원보호림의 자연생태계를 체험한다.','약 6시간 20분','식수 없음(반드시 생수 지참)','만대벌판길 합류지','매점 없음 (도시락 지참 권장)','강원 양구군 해안면 후리 621',38.2901289,128.145418);
