INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000067','둔가리약수숲길','03코스 미산동길','하남리 미기교~하남리 후평교(1.5km)~후평동 수로입구(1.3km)~송개동(2.7km)~대궐농원(1.7km)~바람불이(1.0km)~후평교(2.0km)~개인약수교(1.8km)','강원 인제군','쉬움','10~15Km미만','12','둔.가리약수숲길 1코스 종점인 미기교에서 출발하여 개인약수교까지 총12km의 숲길이다. 가산동마을과 후평동마을을 지나면 내린천을 따라 숲길이 이어진다. 수로를 따라 조성된 이길은 약 3km정도 인가가 없는 구간으로 자연경관이 뛰어나다. 송개동~후평동 구간은 옛 길을 정비한 길이며, 이길도 내린천을 따라 진행된다. 경사가 없어 누구나 쉽게 걸을 수 있다.','4시간 30분','식수보급처가 없으니 매점에서 구입하거나 사전준비','송개동 솔밭','개인약수교 매점','강원 인제군 상남면 하남리 222-15',37.9250651,128.2911574);
