INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000070','산꼬라데이길','솔숲길','예밀정류장 ~ 액자전망대 ~ 모운동(동화마을)','강원 영월군','보통','1Km미만','3.8','솔향으로 가득한 솔숲길은 주문리 모운동 동화마을과 예밀리 포도마을을 이어주는, 사촌 누이와 같은 다정한 길이다. 예밀리에서 출발하여 인생굴곡 굽이길을 지나 평온한 솔숲길을 오르다 보면 어느덧 모운동이 내려다 보이는 산마루엔 추억하듯 먼산의 전경이 겹겹이 펼쳐져 있다.','1시간 30분','김삿갓 면소재지에서 간식, 식수 등은 미리 준비해가는 것이 좋다.','모운동 마을회관','김삿갓 면소재지에서 간식, 식수 등은 미리 준비해가는 것이 좋다.','강원 영월군 김삿갓면 주문리 산 51',37.1372095,128.6198218);
