INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000079','설악누리길','설악누리길','척산족욕공원 ~ 자생식물단지 (2.6km)~ 바람꽃마을(3.4km) ~ 종합운동장 (4.4km) ~ 척산족욕공원(6km)','강원 속초시','쉬움','5~10Km미만','6','설악산 울산바위와 달마봉이 병풍처럼 마을을 둘러싸고 있어 한 폭의 산수화 같이 수려한 경관자원과 설악산에서 자라라는 식물들로 구성된 자생식물원을 연결한 힐링코스로 자연환경에 대한 보존을 원칙으로 친환경적인 최소한의 지원 시실로 자연환경의 혜택을 그대로 느낄 수 있는 코스임. 또한 코스의 마지막인 족욕공원은 누리길 탐방로 인해 피로한 발을 온천수에 담구어 일상의 피로와 한꺼번에 풀수 있는 힐링공간임','1시간 30분','별도 식수보급처는 없으며, 척산 족욕공원에서 구입','척산 족욕공원, 자생식물원, 설악야구장','척산 족욕공원 내 체험관에 음식, 간식 등 구입','강원 속초시 노학동',38.196708,128.5248941);
