INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000082','소이산 생태숲 녹색길','소이산 생태숲 녹색길','소이산입구 ~ 지뢰꽃길(1.3km) ~ 생태숲길(2.7km) ~ 봉수대오름길(0.8km)','강원 철원군','보통','1~5Km미만','4.8','고려시대부터 봉수지로 활용된 소이산은 작지만 큰 산에 버금가는 조망권을 갖고 있고 태초부터 철원의 모든 역사를 품고 있는 철원의 대표 명산으로 한국전쟁이후 민간인 통제구역에 갇혀 수십 년간 일반인의 발길을 거부하며 때묻지 않은 자연 생태환경을 갖추고 있는 곳이다. 녹색길 일부구간의 지뢰지대라는 위협적인 이미지를 벗어나기 위해 아름답고 부드러운 이미지의 꽃에 비유해 “지뢰꽃길”이란 세부 길명을 부여하고 봉수대 오름길을 따라 소이산 정상에 도착하면 한눈에 철원평야와 평강고원을 볼 수 있는 전망대가 자리잡고 있다.','1시 30분','노동당사 매점에서 구입하거나 사전준비','노동당사, 소이산 전망대(정상)','출발점인 노동당사 매점','강원 철원군 철원읍 사요리 산 1',38.255625,127.1856713);
