INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000098','동작충효길','03코스 한강나들길','동작역~한강수변길~흑석역~효사정~용양봉저정~노들역~배수지공원~ 사육신공원~노량진역','서울 동작구','쉬움','1~5Km미만','4.7','한강을 따라 걸을 수 있는 구간으로 사람들이 이용이 많다. 인근 뚝섬과도 연결이 되어있어 가족이나 연인들의 데이트 코스로도 좋다. (동작구 전체를 아우르는 코스로 7개의 코스로 구분)','1시간20분','','4호선 동작역사내, 1호선 노량진역사내, 배수지공원내, 사육신공원내','흑석역, 노들역, 노량진역 주변 편의점','서울 동작구 사당동',37.4945288,126.9744481);
