INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000100','동작충효길','05코스 보라매길','신대방삼거리역~보라매이편한세상~보라매병원입구~보라매생태다리~기상청입구~농심입구~보라매역','서울 동작구','쉬움','1Km미만','2.7','신대방삼거리역에서 성대시장을 지나 보라매공원을 거쳐 보라매역으로 이어지는 보라매길은 문화를 교감하고 소통하는 공간이다. 성대시장을 지나면서 서민들의 애환섞인 삶의 현장을 느낄 수 있다. 보라매공원에는 어린이놀이터, 피크닉장, 청소년수련관, 동작구민체육센터, 노인복지관 등 시설이 갖추어져 있어 남녀노소 다양한 계층에서 문화활동을 교감하고, 소통할 수 있다.','50분','','보라매공원내, 신대방삼거리역사내','보라매공원, 신대방삼거리역 주변 편의점','서울 동작구 사당동',37.4945288,126.9744481);
