INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000119','강서문화 산책길','강서 문화 산책길','양천향교~겸재정선기념관~구암공원~허준박물관','서울 강서구','쉬움','1~5Km미만','2.51','강서구의 명소들을 차례로 들르며 걷게 되는 코스 가장 먼저 양천향교를 지난다. 양천향교는 서울에서는 유일한 것으로, 조선조 태종 12년(1411)에 창건돼 노후된 것을 지난 81년에 전면 복원했다. 향교는 조선시대 우리의 교육 문화를 주도해 온 문화유산으로 유가(儒家)의 공자(孔子)를 비롯한 선성성현 및 선유들의 제사를 모셨으며, 지방 향리들의 자제를 교육하는 오늘날의 국립 중고등과정과 같은 교육기관이었다. 향교를 지나 만나는 견제정선미술관은 우리산천을 표현하는데 가장 적합한 진경산수화풍을 완성한 겸제 정선(1676~1759)의 위대한 예술적 업적을 기리고 진경문화의 계승 발전을 위해 2009년에 개관한 곳이다. 이후 가족과 산책하기 좋은 구암공원을 지나면 허준선생 및 한의학 관련 자료를 보존, 수집, 전시하여 한의학박물관으로 위상을 제고하는 허준박물관에 이르러 코스는 마무리 된다.','90분','겸재정선기념관','겸재정선기념관, 공암나루근린공원','시작점 부근 슈퍼','서울 강서구 가양동',37.5734649,126.8444262);
--행 120
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000120','봉제산 숲나들길','봉제산 숲나들길','담소터~봉제산배드민턴장~등서초등학교운동장~등촌중학교후문길','서울 강서구','보통','1~5Km미만','2.61','능선을 따라 걷기 좋은 숲길이 뻗은 봉제산은 산을 위에서 내려다보면 마치 봉황새가 알을 품고 있는 형국이라는 것에서 이름을 얻었다. 산 정상인 매봉은 백제시절에 봉화터였던 까닭에 아직도 봉화터, 봉수대라는 호칭을 사용한다. 해발 117m에 약 1만㎡인 봉제산은 산을 빙 둘러싸고 주택가가 형성되어 있어 찾는 이들도 많고, 진입도 어느 쪽에서나 가능하다. 주능선과 가지능선으로 걷기 편한 산책로가 뻗었으며, 각 능선의 중턱으로도 실핏줄 같은 숲길이 자글자글하다.','1시간30분','약수터가 있으나 간혹 식수 부적격 판정을 받음','체육공원','코스 내 없음','서울 강서구 화곡동',37.5450822,126.8547418);
