INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000172','용산가족공원 산책길','용산가족공원 산책길','이촌역~국립중앙박물관~거울못~용산가족공원~보신각종~ 이촌역','서울 용산구','매우쉬움','1Km미만','3.3','한국 최대 박물관인 국립중앙박물관을 거쳐 도심 속의 녹색쉼터 용산가족공원을 탐방하는 코스이다. 국립중앙박물관은 우리 역사와 문화의 중요한 가치를 배우고 즐겁게 체험할 수 있다. 이곳에는 구석기시대의 소박한 손도끼에서부터 삼국시대의 화려한 금관, 고려시대의 청자, 조선시대의 회화, 근대의 사진들에 이르기까지 우리의 역사와 문화예술이 한데 모여 있다. 이곳에서 우리 문화의 정수를 마음껏 즐기다보면 자연스럽게 우리문화에 대한 자부심을 느낄 수 있다. 박물관 외부에는 야외정원을 따라 석탑, 석등, 석비 등의 다양한 석조 미술품들을 만나볼 수 있지만 아직까지 박물관의 숨겨진 장소라 할 만큼 많이 알려지지 않은 곳이다. 야외전시장을 따라 용산가족공원이 이어진다. 국립중앙박물관 건립에 따라 지금의 크기는 많이 줄었지만 여전히 찾는 이들이 많은 도심의 녹색쉼터이다.','1시간','사전 준비하거나 매점에서 구입','국립중앙박물관, 용산가족공원, 이촌역','박물과 야외 편의점','서울 용산구 용산동6가',37.5245448,126.9843659);
