INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000185','불암산 하루길','불암산 둘레길','넓은마당 ~(2km)넓적바위 ~ (1.2km)104마을 갈림길 ~ (1.8km)공릉산 백세문 ~ (3.2km)삼육대정문 ~ (2km)삼육대갈림길','서울 노원구','보통','10~15Km미만','11','불암산 둘레길은 덕릉고개 부분만 제외하고는 남녀노소 누구를 막론하고 편안히 걷기에는 최적의 장소이다. 부부, 자녀, 연인의 데이트 코스로도 적극 추천하는 최적의 코스다. 넓적바위 옆에 위치한 학도암과 바위 전면에 새겨진 관음보살도 구경하고 특별히 삼육대 갈림길 코스에 있는 중계동 백사마을로 연결해서 걸으면 앞으로 재개발로 사라지게될 1960년대 서민들이 살았던 이 시대의 마지막 달동네 백사마을을 보는 것을 덤으로 얻을 수 있다. 또한 곳곳에 다양한바위(음바위, 좆바위 등)가 숨겨져 있어 이를 살펴보는 재미도 쏠쏠하다.','4시간','당고개역, 공릉사백세문에서 삼육대정문 가는 코스에 서울여대지나서 음식점도 있고, 매점도 많다.','당고개역, 넓은마당, 경수사, 104마을갈림길 가는 길에 있는 간이 화장실, 공릉백세문 가는길에 간이화장실','경수사','서울 노원구 공릉동 산 222-1',37.6420864,127.0915048);
--행 186
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000186','신반포올레길','신반포 올레길','총 3km (신잠원나들목 -(1.15km) 신반포5차 오솔길 - (1.50km)반포나들목 -(2.1km) 반포한강공원 축구장 - 신잠원나들목(3km)','서울 서초구','매우쉬움','1Km미만','3','잠원나들목을 시작으로 오솔길을 따라 걷다보면 조용히 사색을 하며 걸을 수 있는 메타세콰이어 숲길(신반포아파트 오솔길)이 나온다. 일상의 스트레스를 벗고 숲속의 청량한 공기가 몸과 마음의 휴식을 느끼게 해 준다. 그렇게 숲의 상쾌함을 느끼다보면 어느새 한강으로 이어지는 반포나들목을 만나게 된다. 반포나들목에 들어서면 한강의 시원한 강바람이 먼저 반겨준다. 한강둔치를 걸으며 보이는 한강의 풍경은 잠시 현실의 짐을 내려놓게 한다. 한강을 따라 다시 처음 출발지인 잠원나들목에 도착하면 내 몸이 힐링되어짐을 느낄 수 있다. 신반포 올레길은 아파트단지에서 보기드문 숲의 피톤치드와 한강의 강바람을 함께 느낄 수 있는 도시 힐링로드(healing rod)로 주민의 건강뿐 아니라 휴식의 공간으로 자리잡고 있다.','60분','반포한강공원 편의점 앞','반포한강공원 편의점 앞','반포한강공원 내 편의점 1곳','서울 서초구 잠원동',37.5121264,127.0016423);
