INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000188','토성산성어울길','01코스 몽촌토성길','몽촌토성역~소마미술관~한성백제박물관~몽촌토성~몽촌역사관~성내천~방이습지~마천중앙시장~ 마천역','서울 송파구','매우쉬움','5~10Km미만','7.6','‘토성산성어울길’이란 몽촌토성역에서 시작하여 올림픽공원, 성내천, 마천전통시장을 거쳐 남한산성을 오르는 19.6km의 길이다. 몽촌토성에서 남한산성까지 이어진다 하여 ‘토성산성어울길’이란 정겨운 명칭을 얻었으며, 문화체육관광부 발간 ‘지금 이 길의 아름다움’이란 책자에 소개될 정도로 코스 요소요소에 역사와 문화, 자연과 생태, 전통시장 등 다양하고 매력적인 볼거리를 자랑한다. 역사ㆍ문화자원으로는 한성백제의 고대유산인 몽촌토성과 토성 위에 자리잡은 몽촌역사관, 지난 4월에 개관한 한성백제박물관, 남한산성, 그리고 88올림픽의 열정이 살아있는 올림픽공원, 소마미술관 등이 있다. 자연ㆍ생태 자원으로는 한국의 아름다운 100대 하천으로 선정된 성내천, 자연생태경관지역으로 지정된 방이습지, 그리고 수려한 경관을 자랑하는 남한산이 있으며 이외에도 탐방객들을 유혹하는 전통먹거리가 가득한 마천전통시장이 자리하고 있다.','2시간 30분','코스 곳곳에 편의점이 있으며, 마천중앙시장이 코스에 포함','올림픽공원내 화장실, 성내천 간이화장실, 마천역화장실 등','코스 곳곳에 매점이 있으며, 마천중앙시장이 코스에 포함','경기 광주시 남한산성면 산성리 산 13',37.4808953,127.1860031);
