INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000243','땅끝길','03코스 묵동 갯길','영전(땅끝해뜰마을)~안평~묵동~신홍~서홍~이진(옛 제주뱃길)','전남 해남군','보통','10~15Km미만','11','영전마을을 출발하여 북평면 동쪽 갯가를 지나 이진마을에 이르는 길이다. 영전마을 동쪽 들녘을 지나 안평마을에 이르면 뻘갯벌, 바위갯벌, 모래갯벌이 고르게 펼쳐진 드넓은 바다와 구름위를 나는 철새들을 만날 수 있다. 이진마을은 삼남대로 남쪽 끝지점으로 육지와 제주를 통하는 조선시대 최대의 관문이었다. 이진마을에는 성곽을 비롯하여 돌샘과 제주에서 말을 싣고 올 때 배에 실었던 현무암이 곳곳에 남아있다.','3시간','식수보급처가 없으니 매점에서 구입하거나 사전 준비','영전마을, 묵동마을, 신홍마을, 이진마을','영전마을, 서홍마을, 이진마을','전남 해남군 송지면 통호리 산 167-6',34.3136289,126.5501901);
--행 244
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000244','땅끝길','04코스 쇄노재길','이진(옛 제주뱃길)~달동네~신기~차경~동해~동촌~삼성~만월~흥촌~운전~장수','전남 해남군','보통','15~20Km미만','18','이진마을에서 시작하여 땅끝길 종착지인 장수마을에 이르는 길이다. 두륜산 동편 산자락에 올망졸망 자리한 마을들에는 옛정취가 고스란히 남이있다. 김치마을로 널리 알려진 동해마을은 수백년 된 정자나무와 돌샘, 돌담이 어우러진 전형적인 남도마을의 모습을 간직하고 있고, 두륜산 뒤편 쇄노재는 남도사람들의 애환과 삶의 정서가 가득한 숲길이다. 좌일오일장은 북일면 일대 청정바다에서 갓 건져온 싱싱한 해산물과 이를 팔러 나온, 한 지역에서 오랜 세월 살아온 할머니들과 이야기를 나누는 것도 걷는 여행의 맛일 것이다.','5시간 30분','출발할때 준비하거나, 동해김치마을 돌샘에서 식수를 구할 수 있다.','이진마을, 동해김치마을, 쇄노재휴게소, 북일터미널','이진마을, 쇄노제휴게소, 만월(좌일오일장)','전남 해남군 송지면 통호리 산 167-6',34.3136289,126.5501901);
