INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000245','해남삼남길','01코스 처음길','땅끝마을~도솔암 약수터~통호리마을','전남 해남군','어려움','15~20Km미만','16.9','처음길은 땅끝마을을 시작으로 땅끝탑을 지나 임도숲길을 거니는 코스이다.임도길 중턱에서 내려다 보이는 바다의 모습은 산을 가로질러 누워있는 호수와 같은 형상을 하고 있지만 바다와 산처럼 보이는 섬들이 해안을 따라 누워있는 모습을 볼 수 가 있다. 또한 달마산 밑에 있는 약수터는 많은 사람들이 찾는 장소이며 잠시 쉬어가기 좋은 곳이다. 통호리 마을에는 도시에서 보지 못했던 당집에서 소우언을 빌며 큰 섬처럼 보이는 나무숲에서 쉴 수 있는 곳이다.','6시간','출발지점 땅끝마을 매점에서 구입, 도솔암 약수터','땅끝마을, 도솔암 약수터, 통호마을','땅끝마을, 통호마을','전남 해남군 송지면 송호리 산 192',34.3265557,126.5452054);
--행 246
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000246','해남삼남길','02코스 올망길','통호리마을~영전백화점(해안길, 도로길)~합수부지점~서홍마을','전남 해남군','보통','20~100Km미만','25.7','올망길은 마을과 마을을 잇는 길이다. 3,40여년 전에는 존재했던 길이지만 지금은 도로를 따라 다니다 보니 없어진 통학로이다. 올망길은 이러한 옛길을 찾아 복원한 길이며, 평지와 농로를 따라 자연을 음미할 수 있는 길로 특히 사구미마을에서 영전마을로 넘어가는 길목에는 삼나무 숲이 있어 삼림욕을 할 수 있는 곳으로 코스 내 백미의 장소이다.','4시 00분','식수보급처가 없으니 매점에서 구입하거나 사전 준비','통호마을, 영전마을, 서홍마을','영전백화점, 서홍마을','전남 해남군 송지면 송호리 산 192',34.3265557,126.5452054);
--행 247
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000247','해남삼남길','03코스 해들길','서홍마을(해안길, 농로길)~이진성마을 우물~남창리숲 길~차경마을','전남 해남군','보통','1Km미만','18.6','해들길은 해안을 따라 걸어가는 본격적인 시작점이다. 올방길에서 맛보기로 해안길을 따라 걸었다면 해들길에서 부터는 눈앞에 보이는 완도의 모습과 갯벌, 억새, 갈대가 어우러진 해안길이 눈앞에 펼쳐진다. 해안길은 시간을 잘못 맞추면 길이 막힐 수 있는데, 농로와 숲이 있는 길이 따로 개척되어 선택하여 걸을 수 있다. 비록 짧은 코스이고 전체적으로 아기자기한 길이지만 이진성마을에는 200년 이상 된 오래된 우물처럼 신기한 볼거리가 있으니 천천히 둘러보면서 걸으면 좋은 길이다.','2시간 30분','식수보급처가 없으니 매점에서 구입하거나 사전 준비','이진마을, 차경마을','서홍마을, 이진마을, 차경마을','전남 해남군 송지면 송호리 산 192',34.3265557,126.5452054);
