INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000248','해남삼남길','04코스 나들길','차경마을~오산리 전망대~와룡리 짜우락샘~만수리방파제~내동 노인정~사내호~서초리 사거리','전남 해남군','보통','15~20Km미만','17.4','나들길 부터는 숲길 대신 해안도로와 농로를 이어 해안 옆으로 걸을 수 있는 코스이다. 해남을 벗어나 강진군으로 넘어 가는 약 17km의 해안길로, 다소 지루할 수도 있지만 밀물과 썰물에 따라 풍경이 변하는 모습을 볼 수 있으며, 해남에 널려있는 작은 섬들의 모습, 그리고 와룡리의 바닷가에 솟아나는 민무샘(짜우락샘)을 볼 수 있는 곳이다. 나들길을 벗어나면 더 이상 바다를 볼 수 없기 때문에 바다를 따라 가도록 만들어 놓은 길이다.','5시간','식수보급처가 없으니 매점에서 구입하거나 사전 준비','차경마을, 오산마을, 내동마을','차경마을, 내동마을, 사초마을','전남 해남군 송지면 송호리 산 192',34.3265557,126.5452054);
