INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000249','땅끝 천년숲 옛길','01코스 땅끝길','땅끝마을~도솔암~미황사','전남 해남군','매우어려움','10~15Km미만','15.4','국토순례시발지 땅끝마을 맴섬에서 출발하여 미황사까지 이르는 길이다. 도솔암을 지나 미황사를 가기 위해서는 기암괴석이 병풍처럼 둘러진 달마산의 능선을 따라 움직여야 한다. 능선 중간에 돌맹이가 폭포수처럼 흘러내린 그림같은 아름다운 풍광을 만날 수 있다.','5시간 30분','식수보급처가 없으니 매점에서 구입하거나 사전 준비','땅끝마을, 도솔암, 미황사','땅끝마을','전남 해남군 송지면 송호리 산 178-6',34.3311085,126.548719);
--행 250
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000250','땅끝 천년숲 옛길','02코스 미황사 역사길','미황사 ~ 송촌1제 ~ 송촌, 월송, 증산, 구산, 봉동마을 ~ 현산 봉동계곡 ~ 덕흥마을 ~ 대흥사','전남 해남군','보통','20~100Km미만','18','달마산의 아름다움을 뒤로하고 송촌마을을 지나 여름철 피지로 유명한 봉동계곡에서 잠시 쉬어가도 좋다. 봉동계곡에 지친 몸을 충전하고 덕흥마을을 지나 종점인 대흥사에 도착한다. 대흥사는 통일신라시대에 처음 지어진 절로서, 임진왜란 이후에 서산대사에 의해 많은 인재를 배출하는 큰 절로 성장하였고 지금의 대흥사는 북쪽에서 흘러내리는 금당천을 중심으로 절은 대웅전을 중심으로 하는 북원과 천불전·대광명전을 중심으로 하는 남원으로 나뉘어 다른 절에서는 찾아볼 수 없는 독특한 가람배치를 형성 하고있다.','6시간','식수보급처가 없으니 매점에서 구입하거나 사전 준비','미황사, 현산 봉동계곡, 덕흥마을, 대흥사','미황사 입구에 식당이 하나 있고 미황사 경내에 매점이 하나 있다. 대흥사 경내에 매점이 하나 있고 대흥사 입구에는 식당과 편의점이 많다.','전남 해남군 송지면 송호리 산 178-6',34.3311085,126.548719);
