INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000274','지적산 등산로길','지적산 등산로길','1번코스:목포시 검문소 입구 ~(0.2km)월산교회~(0.6km)도시가스위정상 ~(0.4km)정상 운동시설~(1.2km)석현마을 삼거리~(0.3km)산양마을삼거리 ~(0.4km)남해환경펌프장 2번코스 :석현현대@ ~(0.5km)석현마을삼거리~(1.2km)정상 운동시설~(0.4km)도시가스위정상~(0.6km)월산교회입구','전남 목포시','보통','1~5Km미만','3.5','지적산 등산로는 2012년 10월 급경사지역(석현@ 입구 ~정상부근) 재정비 트레깅 할 수 있는 등산로로 보수하였으며 침목계단을 정비 보다 쉽게 접근할수있게 등산로를 정비하였고 도시가스위쪽 편백숲과 산양마을 편백숲을 정비하여 산림욕을 즐길수 있게하여 보다 쾌적하고 도시일상을 탈피하여 자신만의 휴식공간을 창출하였다. 편백나무숲이 알려 지면서 주민사람들은 물론 전국 각지의 사람들이 찾고 있는 지적산 등산로이다. 목포의 관문 호남정맥 내장산 새재에서 분기하여 달려온 영산기맥은 목포로 들어오기 위해 지적산을 통한다. 지적산 하나만 놓고 보아서는 마을 뒷동산에 불과하겠지만 130여km를 달려온 영산기맥의 마지막 대미를 장식하고 있는 산이름을 가진 중요한 봉우리이다.','2시간 30분','인근 학교 (중앙고등학교)','인근 학교 (중앙고등학교)','현대APT상가슈퍼, 월산교회앞 슈퍼','전남 목포시 석현동',34.8319786,126.4340262);
