INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000277','진도 아리랑길','03코스 아리랑 길','향토문화회관~공설운동장(2.0km)⇒ 운림산방 꼬부랑 숲길(3.5km) ⇒ 삼별초 역사관(0.5km) ⇒ 운림예술촌 벅수길(0.5km) ⇒ 운림산방과 소치기념관, 진도역사관(0.5km) ⇒ 두목재(2.5km) ⇒ 두목재~첨찰산 기상대(2.0km) ⇒ 두목재~회동관광지(신비의 바닷길)','전남 진도군','보통','15~20Km미만','16','진도읍 공설운동장에서 산길을 따라 사천리 광장으로 통하는 아름다운 꼬부랑길이 이어지고 사천리 운림예술촌에 오솔길을 만들어 여러모양의 장승을 양쪽으로 세워놓아 마을 돌담과 함께 아름다운 시골풍경을 만들어 낸다. 남종문인화의 거장 소치 허유의 예술혼이 살아 숨 쉬는 운림산방과 소치일가 화맥 5대를 잇는 소치기념관이 있다. 진도 고유 역사유물과 민속유산을 보존 전승하며 또한 남도전통화맥을 이어온 미술관에서는 전시실과 함께 매주 토요일 미술품경매가 이루어 진다. 진도의 명산 첨찰산으로 오르는 길목에 아리랑비를 세워 민요의 고장으로서 진도의 위용을 자랑하고 첨찰산 울창한 동백숲과 희귀한 생태식물이 무성한 꼬부랑 길을 걸어 회동전망대로 내려와 진도만의 아름다운 해양경관을 볼 수 있으며, 가계해변을 지나 영등사리에 바닷물이 갈라지는 신비의 바닷길을 볼 수 있다.','5시간','아리랑 비, 회동전망대','향토문화회관, 공설운동장, 운림산방, 아리랑비, 회동전망대, 신비의 바닷길','향토문화회관 주변, 운림산방 주변, 신비의 바닷길 입구','전남 진도군 임회면 상만리 171-2',34.3700282,126.2348305);
