INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000278','진도 웰빙등산로','접도(남망산)','수품항 ⇒ 아기밴바위(일출전망대) ⇒ 아홉봉(4.0km) ⇒ 여미주차장 ⇒ 쥐바위 ⇒ 거북바위 ⇒ 병풍바위 계곡 ⇒ 부부느티나무 ⇒ 선달봉삼거리 ⇒ 여미사거리 ⇒ 말똥바위 ⇒ 말똥계곡 ⇒ 작은여미(대도전 촬영지) ⇒ 솔섬해안 ⇒ 솔섬바위 ⇒ 작은여미 ⇒ 동백계곡 ⇒ 여미사거리 ⇒ 맨발체험로 ⇒ 여미주차장 (10km)','전남 진도군','어려움','5~10Km미만','10','접도 웰빙등산로는 의신면에 위치한 작은 섬이지만 다리가 놓여 교통이 원활하며, 해양수산부 선정 아름다운 어촌이자 국가지정 어항인 수품항이 있는 경관이 화려한 마을이다. 이 웰빙등산로는 사면이 바다로 둘러쌓인 해발 150m의 부담스럽지 않은 섬으로 산악회나 가족단위가 등산하기에 최적의 등산 코스이며, 바다를 보며 걸을 수 있어 우리 진도만의 특유의 해양경관을 볼 수 있다. 또한 천연생태 식물들이 많아 젊은층에게도 인기가 많다.','5시간','여미주차장','수품항, 여미주차장','출발점인 수품항 주변에만 있음. 중간에 없음','전남 진도군 의신면 금갑리',34.3782084,126.2927363);
