INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000283','미르마루길','용바위 하늘 길','우주발사전망대~다랭이논~몽돌해변~사자바위~용바위','전남 고흥군','쉬움','1~5Km미만','3.37','고흥우주발사전망대 주변의 주요 관광지를 연결하는 산책로를 말하며, 용바위와 연관해 용의 순우리말인 ''미르''와 하늘의 순우리말인 ''마루''를 합쳐 ''미르마루길''이라 칭하였다. 이 길은 용바위에 얽힌 용의 전설과 수려한 다도해의 리아스식 해안 절경 및 나로호의 발사광경을 한 눈에 볼 수 있는 우주전망대와 하늘의 모습 및 주변 경관이 아름답다.','1시간','식수보급처가 없으니 매점에서 구입하거나 사전준비','통일발원지공원 주차장, 용바위 화장실','우주발사전망대내 매점, 용암마을 매점','전남 고흥군 영남면 우천리 산 155-4',34.5914907,127.4987184);
