INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000294','동강길','01코스','정선역~정선5일장~동강생태공원~동강할미꽃 자생지~동강할미꽃마을(귤암리)~가수느티나무(가수분교)','강원 정선군','보통','20~100Km미만','25','강물 따라 정선8경 둘러보는 길. 정선역을 출발하면 곧 정선의 옛 주거문화를 재현한 아라리촌을 만난다. 강변을 따라 정선읍내로 들어서면 정선오일장이다. 장이 설 때는 풍부한 먹거리와 볼거리가 가득하다. 조양강을 가로지르는 용탄대교를 건너 동강생태공원까지 간다. 정선의 대표적인 볼거리인 가수8경의 하나로 임진왜란 시 주민들이 피난했던 동굴인 나팔굴과 세계 유일종인 동강할미꽃 자생지(4월초 개화)를 지나 잔잔한 강물에 비친 주변 풍경에 취하다 보면, 농촌진흥청에서 선정한‘살고 싶고, 가보고 싶은 농촌마을’로 선정된 할미꽃마을인 귤암리에 다다른다. 여기부터 효자 강아지의 전설이 있는 개바우와 붉은 뼝대(벼랑의 강원도 사투리), 오송정 등 가수8경의 절경들이 차례로 펼쳐진다. 가수리에 이르면 700년 된 느티나무가 늠름하다.','8시간','정선역, 정선오일장 일대','정선역, 정선오일장, 동강생태공원','정선역, 정선오일장 일대, 동강생태공원(야간영업 안함)','경북 경산시 와촌면 동강리 131-2',35.9539348,128.8334728);
--행 295
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000295','동강길','02코스','가수분교→수동쉼터→나리소→제장마을입구→연포분교','강원 정선군','보통','20~100Km미만','20','나리소 기암절벽 감상하며 연포로 가는 길. 가수리부터 동강을 끼고 포장된 도로를 걷지만 차량이 거의 없어 호젓한 여행길이다. 가수8경 중 하나인 금오곡의 작은 폭포를 구경하고 동강 건너편을 보면 기암절벽이 그림처럼 펼쳐진다. 수동 쉼터를 지나 백운산 등산로 입구 갯골마을 앞을 지나면 동강 물길이 벼랑에 막혀 휘돌면서 이루어 놓은 나리소가 강변의 기암절벽, 백운산 자락의 소나무 숲과 기막히게 어우러졌다. 제장마을 앞에서 골덕내길을 30분쯤 걸으면 연포길로 접어든다. 수풀이 우거진 숲길을 지나면 잠수교를 건너 연포분교에 닿는다.','7시간','없음','수동쉼터','가수리','경북 경산시 와촌면 동강리 131-2',35.9539348,128.8334728);
--행 296
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000296','동강길','03코스','문산나루터-쌍쥐바위전망대-장성산 정상-잣봉정상-어라연-만지나루터-전산옥터-동강 삼옥안내소-섶새나루터','강원 영월군','어려움','10~15Km미만','12','잣봉에서 펼쳐진 어라연 비경과 호젓한 강변길. 문산나루터에서 장성산 입구를 향해 출발해 쌍쥐바위전망대에 오른다. 발아래로 비단옷을 입은 듯한 금의마을(문산리)과 동강의 굽이치는 절경이 펼쳐진다. 여기서 1시간쯤 오르면 장성산 정상(694m)에 다다른다. 정상에서 내려와 걷기 좋은 숲길을 따르면 잣봉 정상에 닿는다. 정상에서 비탈과 계단을 한참 내려가면 어라연전망대와 어라연계곡으로 가는 이정표가 나온다. 이어지는 오솔길을 따르면 전산옥주막터가 나오면서 고운 모랫길이 이어진다. 동강 삼옥안내소를 지나면 문산나루터를 떠난 래프팅 배들이 도착하고 이길의 종착지인 섭새나루터이다. 과거 육로가 발달하지 않아 뱃길의 교통 요충지로 사용되던 나루터는 그 흔적만이 남아있지만 동강의 천혜 비경을 벗삼아 관광의 요충지가 되었다.','6시간','없음','문산나루터, 동강 삼옥안내소, 섶새나루터','문산나루터','경북 경산시 와촌면 동강리 131-2',35.9539348,128.8334728);
--행 297
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000297','가거도탐방길','01코스 하늘을 여는 길','달뜰목(시점) - 제1벙커 - 제2벙커 - 샛개재(종점)','전남 신안군','쉬움','1~5Km미만','3','독실산 탐방로의 시작이며, 김부연 하늘공원에서 내려다보는 1구마을 전경이 뛰어나며 해뜰목, 달뜰목 및 능선을 타고 샛개재까지 가는 길은 바다 위를 걷는 듯한 느낌을 주며, 초보자도 부담없이 즐길 수 있는 탐방로이다. 해뜰목, 달뜰목에서 일출을 감상할 수 있으며 또한 달뜰목에서 가거도 동편의 절경을 감상할 수 있다.','7시간','마을 마트 구입','대풍마을회관, 향리마을 회관','없음','전남 신안군 흑산면',34.0565708,125.1358225);
