INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000303','남해 바래길','01코스 다랭이 지겟길','평산항~(6.0km)유구진달래군락지~(1.0km)사촌해수욕장~(1.7km)선구몽돌해변~(1.3km)항촌조약돌해변~(0.8km)항촌전망대~(4.8km)가천다랭이마을~(0.4km)(구)가천초교','경남 남해군','보통','15~20Km미만','16','남해바래길 01코스 다랭이지겟길. 가천 다랭이마을을 중심으로 남해의 수려한 자연환경과 더불어 척박한 생활환경을 극복하기 위한 지겟길을 통하여 우리 선조들의 억척스러운 삶을 느낄 수 있으며, 각종체험(다랭이 논 만들기, 어촌체험 등)과 몽돌해변의 파도를 연인삼아 걸을 수 있는 길이다','5시간','사전준비','유구(삼여도), 사촌해수욕장, 가천정류장','사촌해수욕장 인근, 가천마을','경남 남해군 삼동면 지족리 550-1',34.8288476,127.9954191);
