INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000304','생명의 녹색길','생명의 녹색길','장성호 제방~장성호관광단지 (14km)','전남 장성군','보통','5~10Km미만','14','수려한 자연경관을 자랑하는 장성호를 기점으로 만든 생명의 녹색길 코스는 길이 12km 폭 2m 규모의 길이다. 거대한 장성호댐을 올라오고 나면 생명의 녹색길은 소설 의 주인공인 허칠복의 고향을 생각하며 걷는다.구불구불한 트레킹이 시작된다. 근처에 장성호 문화예술공원, 내장산 국립공원, 백양사, 남창계곡, 입암산성 등을 연계하는 가족관광의 명소로도 안성맞춤이다. 도로진입이나 교통편이 다소 좋지 않아 단점이지만 장성호라는 장점을 가리기엔 너무 작을정도로 길이 아름답다.','3시간 30분','식수보급처가 없으니 매점에서 구입하거나 사전준비','장성댐 관리사무소','주변에 매점이 없으므로 장성읍내에서 준비','전남 장성군 북이면 수성리 산 157-1',35.385686,126.8334937);
