INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000312','남도삼백리길','01코스 순천만 갈대길','해룡와온~(1.1km)해룡 노월~(0.9km)해룡구동~(2.4km)용산전망대~(3.2km)순천만자연생태공원~(1.4km)철새 서식지~(5.1km)별량장산~(0.5km)별량우명~(1.5km)별량화포','전남 순천시','쉬움','15~20Km미만','16','남도삼백리길 제1코스 순천만 갈대길에 오신것을 환영합니다~ 순천만은 세계 5대 연안습지이면서, 2006년 한국관광공사가 국가명승지로 선정한 전국에 제일가는 관광지인 순천만 갈대길은 해넘이 명소 해룡 와온에서 시작하여, 순천만의 절경을 한 눈에 내려다 볼 수 있는 용산 전망대에 오르고, 순천만 갈대숲 사이를 지나 해변길이 아름다운 별량 장산, 우명, 화포로 이어지는 전국 제일가는 걷기 길이예요~ 순천만 갈대길은 총 16킬로미터 거리로 5시간 정도 소요되고, 해발 50미터의 용산 전망대에 이르는 1킬로미터 남짓 되는 산행길이 있어 걷기여행의 기쁨을 더해줍니다. 그럼 순천의 자랑 순천만 갈대길로 떠나 볼까요?','5시간','와온 마을회관, 용산전망대, 순천만 자연생태공원, 별량화포','와온 마을회관, 용산전망대, 순천만 자연생태공원, 별량화포','와온, 순천만 등(관광편의상 사전에 식수 및 간식 준비 필요)','전남 순천시 대대동',34.8778589,127.5132567);
