INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000313','남도삼백리길','02코스 꽃산너머 동화사길','별량화포~(0.6km)죽전방조제~(2.4km)창산~(1km)거차~(1km)거차방조제~(2km)덕산 수문~(5km)용두~(1.5km)구룡~(1.5km)원산~(2km)화산~(1km)죽림~(2km)동화사','전남 순천시','쉬움','20~100Km미만','20','남도삼백리길 제2코스 꽃산너머동화사길에 오신 것을 환영합니다. 꽃산너머 동화사길은 제1코스 순천만갈대길에서 이어지는 두 번째 코스로 별량화포에서 동화사까지 이어지는 멋진 길이예요~ 무엇보다 인상적인 것은 화포에서 용두까지 이어지는 해안 방조제길인데요, 순천만에서와 마찬가지로 철새와 갯벌생물들을 볼 수 있으며, 은은한 바다의 내음을 느낄 수 있답니다. 그리고 이어지는 동화사까지의 마을길은 외갓집을 찾은 포근함이 밀려올거예요~ 갯벌에 들어가고픈 충동을 잘 조절해야 하는 꽃산너머 동화사길은 총 20킬로미터 길이고, 걷는 시간이 7시간정도예요~ 좁은 둑방길이 부담스럽지 않다면 자전거여행도 괜찮아요~ 즐거운 여행 되세요~','7시간','별량 화포 마을회관, 거차뻘배 체험장, 동화사','별량 화포 마을회관, 거차뻘배 체험장, 동화사','별량 화포, 거차뻘배 체험장, 동화사','전남 순천시 대대동',34.8778589,127.5132567);
