INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000315','남도삼백리길','04코스 오치오재길','낙안읍성~(1.5km)상송마을~(2.5km)수정마을~(1.4km)고동치~(3km)고동산~(5.4km)장안치~(0.8km)굴목제~(1km)장군봉~(4.5km)접치제','전남 순천시','어려움','20~100Km미만','20','오늘 제대로 한 번 걸어 보자. 고동산과 조계산 장군봉 두 봉우리를 지나는 20킬로미터의 산행길이라... 맘 먹고 도전이다. 수정마을에서 고동산을 오르지 않고 장안치로 가서 장군봉을 오르는 16킬로미터 코스도 있고, 수정마을에서 고동산만 갔다가 다시 수정마을로 돌아오는 8킬로미터 코스, 혹은 접치에서 장군봉까지만 갔다 오는 12킬로미터 코스도 있다. 풀코스는 20킬로미터이며, 체력이 많이 필요한 코스이다. 그러나 고동산이나 장군봉 하나만 해도 충분히 오를 가치가 있다. 시원하게 내려다 보이는 호남정맥의 준령들은 두고 두고 뇌리에 남아 있으리라. 전체 8시간 코스라고 보여지나 쉬엄 쉬엄 가다 보면 그보다 더 오랜 시간이 걸릴 수 있음을 생각해야 겠다. 오늘 나는 산의 정복하려는 것이 아니라 나의 힐링을 위한 걷기에 나선 거니까~','8시간','낙안읍성, 송광사','낙안읍성, 송광사','낙안읍성, 송광사(관광편의상 사전에 식수 및 간식 준비 필요)','전남 순천시 대대동',34.8778589,127.5132567);
--행 316
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000316','남도삼백리길','05코스 매화향길','접치~(2.3km)두모마을~(4km)흑석마을~(2.3km)유치마을~(1.5km)희야산 녹차밭~(2.8km)노고치~(0.3km)고산마을(녹색농촌체험마을)~(4.3km)군장마을~(5.6km)계월이문(순천향매실마을)','전남 순천시','어려움','20~100Km미만','25','남도삼백리길 제5코스 매화향길에 오신 것을 환영합니다. 접치에서 출발하여 흑석마을 임도를 따라 희야산 녹차밭으로 향하는 길과 녹차밭에서 호남정맥길을 만나 노고치로 내려오는 산행길, 그리고 노고치 바로밑 생태체험마을 고산마을에서 임도를 따라 군장마을을 거쳐 계월마을로 넘어가는 25킬로미터 코스로 풀코스를 종주하는데 10시간 이상이 소여될 것으로 보이는 길이예요. 하지만 차로 금방 닿을 수 있는 거리를 느림여행길로 선택한 것은 빨리빨리에 지친 심신의 힐링을 위한 치유행위잖아요? 느리게 걷다 보면 비로소 보이는 것들이 있는데 그 중 하나가 매화향일거예요~ 멋진 힐링여행 만들어 보아요~','10시간','송광사, 고산마을, 계월이문','송광사, 고산마을, 계월이문','송광사, 고산마을, 계월이문(관광편의상 사전에 식수 및 간식 준비 필요)','전남 순천시 대대동',34.8778589,127.5132567);
