INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000317','남도삼백리길','06코스 삽재팔경길','심원(청소골)~(3km)미초~(1km)회룡~(3km)대치~(1.8km)금평~(1.2km)용서~(5km)구례구역','전남 순천시','보통','10~15Km미만','15','남도삼백리길 제6코스 삽재팔경길에 오신것을 환영합니다~ 이 길은 심원마을에서 구례구역까지 이어지는 한양 옛길구간으로 강변의 마을을 끼고 미초에서 구례가지 이어지는 길이예요~ 옛길이라는 말이 암시를 하듯이 차가 많이 다니지 않고 마을마다 아름다운 옛 정취가 풍부한 무공해 코스라고 할 수 있죠. 미초에서부터 삽재팔동길을 따라 걷게 되는데, 삽재골짜기에 각문, 회룡, 신기, 산령, 구룡, 화평, 덕림, 미초 등 여덟 개의 마을이 있었다는 뜻이랍니다. 전체 15킬로미터로 6시간 정도로 예상되는 이 코스는 산길과 마을길을 따라가는 참으로 운치있는 길이지요. 그럼 남도문화를 걸으면서 느끼는 느림여행, 떠나 볼까요?','6시간','청소골, 구례구역','청소골, 구례구역','청소골, 구례구역(관광편의상 사전에 식수 및 간식 준비 필요)','전남 순천시 대대동',34.8778589,127.5132567);
