INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000318','남도삼백리길','07코스 과거관문길','서문성곽터~(6.8km)서면강천~(5.2km)노은마을~(3km)원동교~(2km)청소~(2km)심원','전남 순천시','쉬움','15~20Km미만','19','오늘은 남도의 문화를 걸으면서 느끼는 느림여행 남도삼백리길 제7코스 과거관문길로 떠나 볼까요? 과거관문길은 순천 시내의 한 복판에서 시작하여 동천을 따라 강청, 청소골을 거쳐 금평까지 이어지는 19킬로미터의 아름다운 걷기길이예요~ 이 길은 원래 순천에서 출발하여 한양으로 가던 옛길이래요~ 어찌나 볼것이 많은지, 그리고 길을 구상한 작가의 상상력이 얼마나 뛰어난지를 감탄하면서 하루를 보내게 될거예요~ 청소골 계곡을 따라 걷게 되기 때문에 시원한 물소리가 끊임없으며 경치 또한 끝내줘요~ 호젓한 걷기길의 정수를 만나실거예요~ 그럼 6시간의 힐링타임으로 들어가 볼까요?','6시간','동천(시내권), 서면강천, 청소골','동천(시내권), 서면강천, 청소골','동천(시내권), 서면강천, 청소골(관광편의상 사전에 식수 및 간식 준비 필요)','전남 순천시 대대동',34.8778589,127.5132567);
