INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000319','남도삼백리길','08코스 동천길','순천만~(4.6km)맑은물관리센터~(7.4km)서문성곽터','전남 순천시','쉬움','10~15Km미만','12','남도삼백리길 제8코스 동천길에 오신것을 환영합니다~ 동천길은 순천시내에서 동천을 따라 순천만까지 가족과 함게 편안하게 걸을 수 있는 구간으로 봄, 여름, 가을, 겨울 팔색조의 아름다움을 가지고 있는 구간이예요~ 1급수 동천은 다양한 물고기와 철새들이 찾는 곳이기도 하고 천변 둔치에는 다양한 화단등을 조성해 놓은가 하면 야생화들이 지천으로 널려 있어 자연의 내음을 맡으면서 걷기에는 최고지요~ 징검다리를 건널때는 물장구도 한 번 쳐 보기도 하고, 아랫장을 지날 때는 국밥을 한 그릇 하고 가기로 해요~ 순천만 자연생태공원에 도착하면 철새들과 이야기도 나누어 보고요. 전체 12킬로미터로 다섯 시간 정도 소요가 되는데 가족끼리 걷기에 최고예요~ 그럼 동천길로 떠나 볼까요?','5시간','동천(시내권), 순천만','동천(시내권), 순천만','동천(시내권), 순천만(관광편의상 사전에 식수 및 간식 준비 필요)','전남 순천시 대대동',34.8778589,127.5132567);
