INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000322','남도삼백리길','11코스 호반벚꽃길','맑은물관리센터~(11km)상사호휴게소~(11km)하수종말처리장~(6km)야생차체험관~(6km)쌍지마을~(11km)맑은물관리센터','전남 순천시','쉬움','20~100Km미만','45','남도삼백리길 제11코스 호반벚꽃길에 오신 것을 환영합니다~ 호반벚꽃길은 맑은물관리센터에서 시작하여 상사호의 오른쪽 편을 끼고가는 상사호길로 선암사에 도착한 다음 다시 상사호의 왼쪽편 857번국도 조정래길로 맑은물 관리센터로 돌아오는 총 45킬로미터의 자전거여행길이예요~ 실제로 이동하는 시간은 3~4시간이면 충분하겠지만 선암사 계곡에 발도 담그고, 망향정이 있으면 올라 고향 잃은 사람들의 아쉬움에 공감도 하다 보면 8시간 정도의 힐링타임이 될예요~ 맑은물관리센터에서 이사천과 같이가는 교량1길을 따라 상사로 접어드는데 자동차가 다니는 길이 아닌 물길 오른편의 마을길을 이용하면 더욱 안전하게 라이딩을 할 수 있답니다. 그럼 아름다운 호반의 벚꽃길로 신나는 라이딩 출발~','8시간','맑은물관리센터, 상사호 휴게소, 하수종말처리장, 야생차체험관','맑은물관리센터, 상사호 휴게소, 하수종말처리장, 야생차체험관','상사호 휴게소, 선암사 야생차 체험관(관광편의상 사전에 식수 및 간식 준비 필요)','전남 순천시 대대동',34.8778589,127.5132567);
