INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000325','남해 바래길','05코스 화전별곡길','천하몽돌해수욕장~(6.4km)나비생태공원~(6.2km)화암교~(1.0km)독일마을~ (1.1km)물건방조어부림','경남 남해군','보통','10~15Km미만','14.7','남해바래길 05코스 화전별곡길. 금산 자락으로 둘러싸인 내산을 중심으로 천하몽돌해변에서 삼동 봉화마을로 이어지는 바다, 산, 강, 들판을 두루 접하면서 자암 김구 선생의 화전별곡의 유유자적한 삶의 느낄 수 있으며, 내산의 편백 숲과 화천의 맑은 물, 원예예술촌, 독일마을, 물건방조어부림을 통해 편안한 휴식과 웰빙의 고장으로 화전(花田) 옛 이름의 본 뜻을 체험할 수 있는 길이다','6시간','사전준비','나비생태공원, 원예예술촌, 물건방조어부림','나비생태공원, 물건마을(종점)','경남 남해군 삼동면 지족리 550-1',34.8288476,127.9954191);
