INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000327','남해 바래길','06코스 말발굽길','창선교~(2.3km)추섬공원~(2.3km)부윤~(1.9km)보현사~ (3.5km)모상개해수욕장~(1.7km)장포~(1.9km)대곡~ (1.0km)적량','경남 남해군','보통','10~15Km미만','14.6','남해바래길 06코스 말발굽길. 빼어난 해안선과 해안을 중심으로 이루어진 어촌마을들의 인심과 맛을 느끼며, 고려시대 때부터 군마를 기르던 지역으로 현재 유적 등은 많이 남아있지 않지만 말발굽모양의 지형과 적량성터를 통하여 선조들의 삶을 느낄 수 있는 길이며, 이 구간은 자전거도로로 이용가능하다.','5시간','사전준비','적량','창선교입구','경남 남해군 삼동면 지족리 550-1',34.8288476,127.9954191);
