INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000329','남해 바래길','07코스 고사리밭길','적량~(4.2km)천포~(1.5km)가인~(1.8km)고두~ (2.1km)식포~(4.4km)동대만휴게소','경남 남해군','보통','10~15Km미만','14','남해바래길 07코스 고사리밭길. 고사리로 유명한 창선도의 정취를 느끼고, 산과 밭으로 거미줄처럼 이어진 고사리밭길을 통해 아름다운 해안과 바다를 내려다 볼 수 있으며, 선사시대의 공룡발자국화석을 따라 걸으며 시간여행을 할 수 있는 길이다','4시간30분','사전준비','적량, 동대만휴게소','동대만휴게소','경남 남해군 삼동면 지족리 550-1',34.8288476,127.9954191);
