INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000330','천지인 둘레길','01코스 읍성길','토요시장→홍살문→삐비정→북문→장원봉→문예회관→예양공원→토요시장','전남 장흥군','보통','5~10Km미만','6.7','장흥읍성을 중심으로 개발한 길이다. 장흥의 구시가지와 신시가지를 비교하며 돌아봄으로서 장흥의 역사와 발전상을 한 눈에 살필 수 있고, 성을 에워싸고 도는 예양강과 함께 어우르는 산 과 들 등 자연환경을 돌아 볼수 있는 둘레길이다. 남도의 맛깔스런 음식과 전통시장의 멋이 어울려 옛 추억과 즐길거리가 많은 정남진장흥토요시장을 경유한다. 또한 사계절 꽃이 피는 탐진강의 아름다음을 한눈에 바라보며 토성위를 걷는 체험을 할 수 있다.','1시간 30분','식수보급처가 없으니 매점에서 구입하거나 사전 준비','토요시장,예양공원','읍내','전남 장흥군 장흥읍 영전리 167-1',34.6642785,126.8849612);
