INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000331','천지인 둘레길','02코스 동학길','장원봉→동학전망대2→동학전망대1→공설운동장','전남 장흥군','보통','1~5Km미만','3','동학길은 장원봉에서 동학농민혁명탑을 거쳐 금강천(감내)까지의 길로 총 연장 3.0km이다. 이 길은 장흥향교의 주산인 거마봉의 능선과 동학혁명답을 거쳐 동학군이 최후 혈전을 벌인 들녁과 탐진강의 많은 물이 흐름을 느끼며 걸을 수 있는 길이다. 장흥 팔경의 하나인 탐진강 저녁노을의 아름다움을 볼 수 있다.','1시간','식수보급처가 없으니 매점에서 구입하거나 사전 준비','공설운동장','읍내','전남 장흥군 장흥읍 영전리 167-1',34.6642785,126.8849612);
