INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000332','천지인 둘레길','03코스 에음길','동학농민기념탑→금강천벚꽃길→탐진강변→토요시장','전남 장흥군','쉬움','5~10Km미만','9','에음길은 말 그대로 굽이굽이 굽어있는 길입니다. 벚꽃과 개나리,봄의 정취와 탐진강과 금강천의 사계절을 느끼며 한들평야와 억불산이 한눈에 보이는 옛 서민들의 삶을 느낄 수 있는 길입니다. 특히 봄이오면 벚꽃과 개나리의 화려한 만개를 만끽 할 수 있는 아름다운 길입니다.','3시간','식수보급처가 없으니 매점에서 구입하거나 사전 준비','공설운동장, 토요시장','읍내','전남 장흥군 장흥읍 영전리 167-1',34.6642785,126.8849612);
--행 333
