INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000355','천년 솔바람길','02코스','문헌서원 → 기린봉(0.5㎞) → 옛 문헌서원터(0.6㎞) → 박경수선생 생가터(1㎞) → 이상재선생생가지(1.2㎞) → 호암리마을(1.5㎞) → 봉서사(1㎞) → 건지산성(0.3㎞) → 문헌서원(1.3㎞)','충남 서천군','보통','5~10Km미만','7.5','솔바람길을 따라 서천의 역사·문화여행길. 문헌서원, 봉서사, 건지산성, 박경수선생 생가터 등과 연결됨 - 문헌서원 : 고려말 대학자 가정 이곡과 목은 이색 두분을 배향하기 위해 조선 중기 건립된 사액서원 - 건지산성 : 건지산의 정상부근을 에워싼 말안장 모양의 내성과 그 서북쪽 경사면을 둘러싼 외성의 2중 구조로 되어있는 산성이다. 성을 쌓은 시기는 백제 말에서 통일신라 전기로 보고 있으며, 백제 부흥운동의 거점이었던 주류성으로 추정하고 있다.','','','','','충남 서천군 한산면 호암리 252',36.0925244,126.8003604);
