INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000356','관대바위 산소길','총 05코스','고성체육관~수성샘터~고성산 정상~차선바위~금성농원입구~금수촌교~고성예비군훈련장~고성체육관','강원 고성군','어려움','5~10Km미만','9.2','강원고성갈래구경길 제6경 관대바위 산소길. 진부령 정상(해발520m)에서 미술관을 관람하고 동해바다가 있는 곳 동족 방향 북천교까지의 코스이었지만 임도구간 통제로 고성산둘레길로 변경되었다. 고성산둘레길은 고성군 소재지에서 고성산둘레와 정상을 거처 다시 고성군 소재지로 되돌아오는 코스로 고성산 정상에서 동해와 태백산맥의 흐름을 한눈에 볼 수 있어 독특한 풍광을 자아낸다.','3시간','','고성군체육관','고성군 소재지 일대','강원 고성군 간성읍 탑동리 산 72-1',38.3515631,128.4509355);
