INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000362','홍천9경 생태탐방로','01코스','두촌면 가리산~용소계곡 ~백우산~쌍계사~내촌면 가령폭포','강원 홍천군','어려움','20~100Km미만','37','발아래로 펼쳐진 소양호의 풍경과 3개 암봉으로 이루어진 정상은 석간수가 샘솟으며, 강원 제1의 전망대로 할 만큼 풍경이 좋다. 우거진 숲과 곡곳에 펼쳐지는 소와 너내바위 등 맑은 물과 기암괴석이 10km의 계곡으로 조화롭게 펼쳐져 비경을 자랑하는 홍천7경인 용소계곡은 내설악에 버금가는 아름다운 계곡을 자랑한다. 또한 홍천의 5경 중의 하나인 가령폭포는 오지의 백암산 서남쪽 기슭에 숨어 있으며, 개령호인들이 찾으면서 알려지기 시작한 백암산과 더불어 우렁찬 굉음을 토하며 수십미터(50m)의 낭떠러지를 뒤흘들며 내리꽂는 자태가 웅장하다','6시간','식수는 별도로 준비하여야 하며, 탐방로 주변 가게에서 구입','화장실은 가리산 주차장내, 용소계곡 출발점 및 도척점에 간이화장실','탐방로 주변 작은 규모의 가게가 있음.','강원 홍천군 내촌면 광암리 산 11-15',37.8579192,128.0718189);
--행 363
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000363','너브내 수변탐방로','01코스','홍천시외버스터미널 - (4.04km)화계초등학교 앞 삼거리 - (1.04km)소매곡교 - (2.86km)천냥바위 - (1.01km)도사곡교- (3.50km)장항리 강변 - (4.57km)남노일리 강변유원지 - (10.66km)팔봉산관광지','강원 홍천군','어려움','20~100Km미만','28','북방면 도사곡리는 모래벌과 굴곡이 아름답고 ‘돈을 갚지 못한 친구가 친구를 바위로 불러 술잔을 나누며 빚을 갚을 수 없게 되어 죽으려 하자, 만류하며 아무리 못 갚는다 해도 친구의 목숨을 원하겠나, 나중에 벌면 갚아도 된다’는 말 한마디로 천냥 빚을 갚은 전설이 깃든 ‘천냥바위’가 있다. 산의 삼면을 둘러싼 팔봉산은 여덟 개의 봉우리로 길게 뻗어 있으며 2봉에는 삼부인당이라는 당집이 있고, 3봉이 가장 높고 4봉에는 해산바위굴이 있으며 조망이 가장 뛰어나 홍천강과 삼악산이 한눈에 가득 들어온다. 홍천강은 캠핑과 함께 쏘가리와 토종 물고기들이 많아 낚시군들도 즐겨찾는 강이다.','9시간 30분','식수는 별도로 준비하여야 하며, 탐방로 주변 가게에서 구입','홍천종합버스터미널, 팔봉산관광지','탐방로 주변 작은 규모의 가게가 있음.','강원 홍천군 서면 개야리 244-4',37.6886434,127.6316658);
