INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000365','물소리길','01코스','양수역→목왕로앞→월계골입구→목왕로편의점→부용교→산양삼산밭입구→한음이덕형신도비→전원일기마을쉼터(부용산입구)→몽양여운형생가기념관→신원역→강변산책로→양서초등학교→도곡터널→ 제방길삼거리→국수역','경기 양평군','보통','10~15Km미만','13.8','물소리길은 자연을 사랑하는 도보여행객들을 위한 길입니다. 다정하게 속삭이는 포근함이 그리울 땐 언제든지 물 맑은 양평을 찾아 남한강과 북한강변을 아늑하게 감돌아 흐르듯 산책하는 여유를 즐기세요. 물소리길은 우리나라의 대표적인 도보여행길인 제주올레와 협력하여 탄생하였으며, 남녀노소 누구나 걸을 수 있고, 자연과 문화를 이해하며 걷는 길입니다. 자연경관이 잘 보전된 야트막한 산과 완만한 땅이 선물하는 풍경을 만나면 고향같은 동네를 지나는 사람 모두 정단운 얼굴로 눈인사 나누게 됩니다. 이 길을 걸으며 양평의 물처럼 맑은 삶의 행복을 느껴 보세요.','4시 30분','부용산약수터','양수역, 산양삼산밭입구, 신원역','양수역, 목왕로편의점, 국수역','경기 양평군 옥천면 아신리 545-21',37.5146635,127.4377255);
